import React, { Component } from 'react';
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'
import { removeItem,subtractQuantity,addQuantity} from './../../redux/actions/cartActions';
import  CartHelper from './CartHelper'
import {getProduct }from './../../service/ProductStorage';
import './Cart.css';
class Cart extends Component{
    constructor(props){ 
        super(props);
        this.state = {
            productsInfo: getProduct()
          };

    }
    //to remove the item completely
    handleRemove = (id)=>{
        this.props.removeItem(id);
    }
    //to add the quantity
    handleAddQuantity = (id)=>{
        this.props.addQuantity(id);
    }
    //to substruct from the quantity
    handleSubtractQuantity = (id)=>{
        this.props.subtractQuantity(id);
    }
    render(){
        console.log("nfro ",this.state.productsInfo.addedItems)
        let addedItems = this.state.productsInfo.items.length ?
            (  
                this.state.productsInfo.addedItems.map(item=>{
                    return <ul class="list-group">
                        <li class="list-group-item d-flex justify-content-between align-items-center">
                          <div class="small-image">
                            <img src={item.img} alt={item.img} className="" />
                          </div>
                          <div>
                          <span className="title">{item.title}</span>
                          </div>
                          <span class="badge badge-primary badge-pill">
                            12
                          </span>
                        </li>
                      </ul>;
                })
            ):

             (
                <p>Nothing.</p>
             )
       return(
            <div className="container">
                <div className="cart">
                    <h5>List of what you have ordered</h5>
                    <ul className="">
                        {addedItems}
                    </ul>
                </div> 
                <CartHelper />          
            </div>
       )
    }
}


const mapStateToProps = (state)=>{
    console.log("state " ,state)
    return{
        items: state.addedItems,
        addedItems: state.addedItems
    }
}
const mapDispatchToProps = (dispatch)=>{
    return{
        removeItem: (id)=>{dispatch(removeItem(id))},
        addQuantity: (id)=>{dispatch(addQuantity(id))},
        subtractQuantity: (id)=>{dispatch(subtractQuantity(id))}
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(Cart)
